import React, { useState } from 'react';
import {loginUser} from '../services/userAuth';
import './Login.css';
import axios from "axios";

const LoginUser = () => {

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [alertBox, setAlertBox] = useState({state:false, message:'',type:'error'});

    async function sendLoginRequest(e, setAlertBox) {
        e.preventDefault();
        const reqBody = {
            username: username,
            password: password,
        };
            const loginResponse = await axios.post('http://localhost:9067/person/login', reqBody);
            const {loggedInData} = loginResponse.data;
            localStorage.setItem("token", loggedInData.token);

        // try {
