import react from 'react'
import React, {useState} from 'react'
import "./header.css"
import { ImageSlider } from './ImageSlider'
import BtnSlider from "./BtnSlider"


export default function Header({slides}) {

    const [slideIndex, setSlideIndex] = useState(1)

    const nextSlide = () => {
        if(slideIndex !== ImageSlider.length) {
            setSlideIndex(slideIndex + 1)
        }
        else if (slideIndex === ImageSlider.length) {
            setSlideIndex(1)
        }
    }

    const prevSlide= () => {
        if(slideIndex !== 1) {
            setSlideIndex(slideIndex - 1)
        }else if (slideIndex === 1){
            setSlideIndex(ImageSlider.length)
        }
    }


  return (
    <div className="header">
        <div className="wrapper1">
            <div className="left">

                <section className="slider">

                    {ImageSlider.map((slide, index) => {
                        return (
                            <div className={slideIndex === index + 1 ? "slide active-anim" : "slide"}>
                                <img src={slide.image} alt="fitness image" />
                            </div>
                    )
                })}
                <BtnSlider moveSlide = {nextSlide} direction= {"next"}/>
                <BtnSlider moveSlide = {prevSlide} direction={"prev"}/>
                </section>
            </div>
            <div className="right">
                <div className="wrapper_right_header">
                    <h1>
                        Your go to online fitness trainer and nutritionist.
                    </h1>
                    <p>
                        After getting a bachelors in nutrtion and fitness training, 
                        I started working with people all around the world and changing their lives.
                    </p>
                    <div className=""></div>
                </div>
            </div>
        </div>
    </div>
  )
}
